import { Table, Modal } from "../component/Component";
import { useState, useEffect } from "react";


function Siswa(props) {
  const [modalName, setModalName] = useState(props.modalName ? "insert" : "");
  const [data, setData] = useState("");
  const [siswa, setSiswa] = useState([]);

  const getDataSiswa = () => {
    fetch("http://localhost/pkl/Project-UKS/backend/proses/tampil_data_siswa.php")
      .then(res => res.json())
      .then(data => setSiswa(data))
      .catch(err => console.error("Gagal ambil data siswa:", err));
  };
  

  const funcModal = () => {
    if (props.setModal) {
      props.setModal(!props.modal);
    }
  };

  console.log("modalName:", modalName, "data:", data);

  return (
    <div className="w-full h-screen flex">
      <Table
        setM={funcModal}
        cari={props.cari}
        funcName={props.setModalName}
        setData={props.setData}
        data={siswa}
      />
      <Modal
        setM={funcModal}
        name={props.modalName}
        data={props.data}
        title={
          props.modalName === 'edit'
            ? 'Edit data siswa'
            : props.modalName === 'delete'
              ? 'Hapus data siswa'
              : props.modalName === 'insert' && 'Tambah data siswa'
        }
        stat={props.modal}
        onSubmit={(form) => {
          const formBody = new URLSearchParams();
          for (const key in form) {
            formBody.append(key, form[key]);
          }

          fetch('http://localhost/pkl/Project-UKS/backend/proses/proses_edit.php', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: formBody.toString()
          })
            .then((res) => res.json())
            .then((result) => {
              if (result.status === "success") {
                getDataSiswa();
                props.setModal(false);
                window.location.reload();
              }
            })
            .catch((err) => {
              console.error("Error:", err);
            });
        }}
      />
    </div>
  );
}
export default Siswa;
