import React from "react";
import { useEffect, useState } from "react";
import { Button, SideBar } from "./component/Component";
import logo from "./component/Logo-UKS-Usaha-Kesehatan-Sekolah-Warna.png";
import { useNavigate } from "react-router-dom";

function Dashboard() {
    const navigate = useNavigate();
    const [view, setView] = useState("home");
    const [nav , setNav] = useState(false);

    const toggleNav = () => {
        setNav(!nav);
    }
    useEffect(() => {
        if (localStorage.getItem("isLogin") !== "true") {
            window.location.href = "/";
        }
    }, []);
    const logot = () => {
        localStorage.setItem("isLogin", "false");
        localStorage.removeItem("username");
        navigate("/");
    }
    return(
        <div className="w-full h-screen flex justify-center items-center bg-gray-300">
            <div className={`${nav ?'w-16' :'w-1/6'} transition-all duration-300 ease-in-out fixed left-0`}>
                <SideBar nav={nav} logo={logo} logot={logot}/>
            </div>
            <div className={`${nav ?'w-full ml-16' :'w-5/6 ml-64'} transition-all duration-300 ease-in-out h-full flex flex-col`}>
                <div className="w-full pt-4 flex justify-between">
                    <div>
                        <Button color="transparent items-center" onClick={toggleNav}>
                            <i className="bi bi-list text-2xl"></i>
                        </Button>
                    </div>
                    <h1 className="text-3xl font-bold mb-4">Dashboard</h1>
                    <p className="text-lg">Welcome, {localStorage.getItem("username")}&nbsp;&nbsp;</p>
                </div>
            </div>
        </div>
    )
}
export default Dashboard;
