import { useEffect, useState } from "react";
import { Button, SideBar } from "./component/Component";
import logo from "./component/Logo-UKS-Usaha-Kesehatan-Sekolah-Warna.png";
import Home from "./view/home";
import Profil from "./view/profil";
import Setting from "./view/setting";
import { useNavigate } from "react-router-dom";
import './App.css'

function Dashboard() {
    const navigate = useNavigate();
    const [view, setView] = useState("home");
    const [nav , setNav] = useState(false);
    const [hober, setHober] = useState(true);
    const [modal, setModal] = useState(false);

    const funcModal = () => {
        setModal(!modal);
    }

    const toggleNav = () => {
        setNav(!nav);
    }
    const toggleHober = () => {
        setHober(!hober);
    }
    useEffect(() => {
        if (localStorage.getItem("isLogin") !== "true") {
            window.location.href = "/";
        }
    }, []);
    const logot = () => {
        localStorage.setItem("isLogin", "false");
        localStorage.removeItem("username");
        navigate("/");
    }
    return (
        <div className="w-full min-h-screen flex flex-col md:flex-row bg-gray-300">
            {/* Sidebar */}
            <div
                onMouseEnter={toggleHober}
                onMouseLeave={toggleHober}
                className={`
                    ${nav && hober ? 'w-16' : 'w-3/4 md:w-1/6'}
                    h-16 md:h-screen
                    shadow-black shadow-lg
                    transition-all duration-300 ease-in-out
                    fixed md:static top-0 left-0 z-20
                    md:z-auto
                `}
            >
                <SideBar
                    className="md:relative"
                    view={view}
                    nav={nav}
                    hober={hober}
                    logo={logo}
                    setView={setView}
                    logot={logot}
                />
            </div>
            {/* Overlay for mobile when sidebar open */}
            {nav && (
                <div
                    className="fixed inset-0 bg-black bg-opacity-30 z-10 md:hidden"
                    onClick={toggleNav}
                />
            )}
            {/* Main Content */}
            <div
                className={`
                    flex-1 flex flex-col
                    transition-all duration-300 ease-in-out
                    mt-16 md:mt-0
                    ${nav && hober ? 'ml-16 md:ml-0' : 'ml-0 md:ml-[16.666667%]'}
                `}
            >
                <div className="w-full flex justify-between items-center px-4 py-2">
                    <Button color="transparent items-center" onClick={toggleNav}>
                        <i className="bi bi-list text-xl"></i>
                    </Button>
                    <div className={`relative flex flex-col items-end`}>
                        <p
                            onClick={funcModal}
                            className="text-lg font-bold bg-white shadow-black hover:shadow-md mb-4 rounded-2xl items-center transition-all duration-300 ease-in-out uppercase px-4 py-2 cursor-pointer"
                        >
                            <i className="bi bi-person-fill bg-yellow-500 rounded-full my-2 ml-2"></i> {localStorage.getItem("username")}
                        </p>
                        <Button
                            color={`red-500 ${modal ? '' : 'hidden'} absolute z-10 bottom-0`}
                            textColor="white"
                            onClick={logot}
                        >
                            <i className="bi bi-box-arrow-right"></i>Log Out
                        </Button>
                    </div>
                </div>
                <div className="w-full flex-1 bg-white rounded-lg p-4 shadow-lg overflow-auto">
                    {view === "home" ? <Home /> : view === "profil" ? <Profil /> : view === "setting" && <Setting />}
                </div>
            </div>
        </div>
    )
}
export default Dashboard;
