import { Table, Modal } from "../component/Component";
import { useState } from "react";

function Siswa(props) {
  const [modalName, setModalName] = useState(props.modalName ? "insert" : "");
  const [data, setData] = useState("");


  const funcModal = () => {
    if (props.setModal) {
      props.setModal(!props.modal);
    }
  };

  return (
    <div className="w-full h-screen flex">
      <Table setM={funcModal} cari={props.cari} funcName={setModalName} setData={setData} />
      <Modal
        setM={funcModal}
        name={modalName}
        data={data}
        title={
          modalName === 'edit'
            ? 'Edit data siswa'
            : modalName === 'delete'
              ? 'Hapus data siswa'
              : props.modalName === 'insert' && 'Tambah data siswa'
        }
        stat={props.modal}
        onSubmit={(form) => {
          fetch('http://localhost/pkl/Project-UKS/backend/proses/proses_edit.php', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form)
          })
            .then(res => res.json())
            .then(result => {
              console.log("Berhasil edit:", result);
              props.setModal(false);
            })
            .catch(err => {
              console.error("Error:", err);
            });
            console.log("Data yang dikirim:", form);
        }}
      />
    </div>
  );
}
export default Siswa;
